public class ArrayDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] cars = new String[4];
		cars[0] = "BMW";
		cars[1] = "Mercedes-Benz";
		cars[2] = "Audi";
		cars[3] = "Toyota";
		
		System.out.println("Car Brand 1 is: " + cars[0]);
		System.out.println("Car Brand 2 is: " + cars[1]);
		System.out.println("Car Brand 3 is: " + cars[2]);
		System.out.println("Car Brand 4 is: " + cars[3]);
		
	}

}
